import React, { useState, useEffect } from "react";
import axios from "axios";

const ExchangeBooks = () => {
    const [offeredBooks, setOfferedBooks] = useState([]);
    const [requestedBooks, setRequestedBooks] = useState([]);
    const [exchangeDetails, setExchangeDetails] = useState({
        offeredBook: "",
        requestedBook: "",
        message: "",
    });

    useEffect(() => {
        // Fetch offered books from the backend
        const fetchOfferedBooks = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/browse"); // Endpoint to get available books
                setOfferedBooks(response.data);
            } catch (error) {
                console.error("Error fetching offered books:", error);
            }
        };

        // Fetch requested books from the backend (if you have a separate endpoint)
        const fetchRequestedBooks = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/browse"); // Same endpoint for simplicity
                setRequestedBooks(response.data);
            } catch (error) {
                console.error("Error fetching requested books:", error);
            }
        };

        fetchOfferedBooks();
        fetchRequestedBooks();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setExchangeDetails((prevDetails) => ({
            ...prevDetails,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:5000/api/exchange", exchangeDetails);
            console.log("Exchange request submitted:", response.data);
            // Reset form
            setExchangeDetails({
                offeredBook: "",
                requestedBook: "",
                message: "",
            });
        } catch (error) {
            console.error("Error exchanging book:", error);
            alert("Failed to exchange book. Please try again."); // User feedback
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 py-10">
            <h2 className="text-3xl font-bold text-center mb-6">Exchange Your Book</h2>
            <form onSubmit={handleSubmit} className="max-w-md mx-auto bg-white p-8 rounded-md shadow-md">
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">Offered Book</label>
                    <select
                        name="offeredBook"
                        value={exchangeDetails.offeredBook}
                        onChange={handleChange}
                        required
                        className="border rounded w-full py-2 px-3 text-gray-700"
                    >
                        <option value="">Select a book...</option>
                        {offeredBooks.map((book) => (
                            <option key={book.id} value={book.id}>
                                {book.title} by {book.author}
                            </option>
                        ))}
                    </select>
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">Requested Book</label>
                    <select
                        name="requestedBook"
                        value={exchangeDetails.requestedBook}
                        onChange={handleChange}
                        required
                        className="border rounded w-full py-2 px-3 text-gray-700"
                    >
                        <option value="">Select a book...</option>
                        {requestedBooks.map((book) => (
                            <option key={book.id} value={book.id}>
                                {book.title} by {book.author}
                            </option>
                        ))}
                    </select>
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">Message (optional)</label>
                    <textarea
                        name="message"
                        value={exchangeDetails.message}
                        onChange={handleChange}
                        className="border rounded w-full py-2 px-3 text-gray-700"
                    />
                </div>
                <button
                    type="submit"
                    className="w-full bg-blue-600 text-white font-bold py-2 px-4 rounded hover:bg-blue-700"
                >
                    Exchange Book
                </button>
            </form>
        </div>
    );
};

export default ExchangeBooks;
