// src/components/Header.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useUserContext } from '../context/UserContext';

const Header = () => {
  const { user, logout } = useUserContext();
  const [loginMessage, setLoginMessage] = useState('');

  const handleLogout = () => {
    logout();
    setLoginMessage('You have logged out successfully.');
  };

  return (
    <header className="bg-background text-white shadow-md sticky top-0 z-50">
      <nav className="container mx-auto p-4 flex justify-between items-center">
        <div className="text-3xl font-extrabold text-primary">Book Exchange Platform</div>
        <ul className="flex space-x-6">
          <li><Link to="/" className="hover:text-primary transition duration-300">Home</Link></li>
          <li><Link to="/browse" className="hover:text-primary transition duration-300">Browse Books</Link></li>
          <li><Link to="/list" className="hover:text-primary transition duration-300">List a Book</Link></li>
          <li><Link to="/profile" className="hover:text-primary transition duration-300">Profile</Link></li>
          {user ? (
            <li onClick={handleLogout} className="cursor-pointer hover:text-primary transition duration-300">Logout</li>
          ) : (
            <li><Link to="/login" className="hover:text-primary transition duration-300">Login/Register</Link></li>
          )}
        </ul>
      </nav>
      {loginMessage && <p className="text-green-500">{loginMessage}</p>} {/* Display logout message */}
    </header>
  );
};

export default Header;
