import { useState } from 'react';
import { auth, googleProvider } from '../firebase';
import { createUserWithEmailAndPassword, signInWithPopup } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';

const Signup = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      navigate('/');
    } catch (error) {
      console.error('Error during signup:', error.message);
    }
  };

  const handleGoogleSignup = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      navigate('/');
    } catch (error) {
      console.error('Error with Google signup:', error.message);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold">Signup</h2>
      <form onSubmit={handleSignup} className="mt-4">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="block w-full mb-3 p-2 border"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="block w-full mb-3 p-2 border"
        />
        <button type="submit" className="bg-blue-500 w-full p-2 rounded">Sign Up</button>
      </form>

      <button onClick={handleGoogleSignup} className="mt-4 bg-red-500 w-full p-2 rounded">
        Sign Up with Google
      </button>
    </div>
  );
};

export default Signup;
