// src/context/UserContext.jsx
import React, { createContext, useContext, useState } from 'react';
import axios from 'axios';

const UserContext = createContext();

export const UserProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  const signup = async (email, password, name, bio, location) => {
    try {
      const response = await axios.post('http://localhost:4000/routes/authRoutes/signup', { email, password, name, bio, location });
      setUser(response.data);
    } catch (error) {
      console.error("Signup error:", error);
    }
  };

  const login = async (email, password) => {
    try {
      const response = await axios.post('http://localhost:4000/routes/authRoutes/login', { email, password });
      setUser({ uid: response.data.uid, token: response.data.token });
    } catch (error) {
      console.error("Login error:", error);
    }
  };

  const logout = () => {
    setUser(null);
  };

  return (
    <UserContext.Provider value={{ user, signup, login, logout }}>
      {children}
    </UserContext.Provider>
  );
};

export const useUserContext = () => useContext(UserContext);
