// src/components/HeroSection.jsx
import React from 'react';

const HeroSection = () => {
  return (
    <section className="bg-gray-100 py-10">
      <div className="container mx-auto text-center">
        <h1 className="text-5xl font-bold text-blue-600 mb-4">Welcome to the Book Exchange Platform</h1>
        <p className="text-gray-700 text-lg mb-6">
          Exchange, lend, or donate books easily and connect with readers nearby.
        </p>
        <button className="bg-blue-500 hover:bg-blue-700 text-white py-3 px-8 rounded shadow">
          Get Started
        </button>
      </div>
    </section>
  );
};

export default HeroSection;
