// src/components/BookManager.jsx
import React, { useEffect, useState } from "react";
import axios from 'axios';
import { useUserContext } from "../context/UserContext";

const BookManager = () => {

  const { user } = useUserContext(); // Access user from context
  const userId = user ? user.uid : null; // Get userId if user is logged in

  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({
    title: "",
    author: "",
    description: "",
    condition: "",
    category: "",
    photos: [],
    location: "",
  });

  // Fetch books for the specific user
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/api/books/user/${userId}`);
        setBooks(response.data);
      } catch (error) {
        console.error("Error fetching books:", error);
      }
    };

    if (userId) {
      fetchBooks();
    }
  }, [userId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewBook((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:4000/api/books/add', { userId, ...newBook });
      setBooks((prev) => [...prev, { ...newBook, userId }]);
      setNewBook({
        title: "",
        author: "",
        description: "",
        condition: "",
        category: "",
        photos: [],
        location: "",
      });
    } catch (error) {
      console.error("Error adding book:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/books/${id}`);
      setBooks((prev) => prev.filter((book) => book.id !== id));
    } catch (error) {
      console.error("Error deleting book:", error);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Manage Your Books</h2>

      <form onSubmit={handleSubmit} className="mb-4">
        <input
          type="text"
          name="title"
          value={newBook.title}
          onChange={handleChange}
          placeholder="Title"
          required
          className="border rounded p-2 mr-2 text-black"
        />
        <input
          type="text"
          name="author"
          value={newBook.author}
          onChange={handleChange}
          placeholder="Author"
          required
          className="border rounded p-2 mr-2 text-black"
        />
        <textarea
          name="description"
          value={newBook.description}
          onChange={handleChange}
          placeholder="Description"
          required
          className="border rounded p-2 mr-2 w-full text-black"
        />
        <input
          type="text"
          name="condition"
          value={newBook.condition}
          onChange={handleChange}
          placeholder="Condition (e.g., New, Used)"
          required
          className="border rounded p-2 mr-2 text-black"
        />
        <input
          type="text"
          name="category"
          value={newBook.category}
          onChange={handleChange}
          placeholder="Category (e.g., Fiction, Non-Fiction)"
          required
          className="border rounded p-2 mr-2 text-black"
        />
        <input
          type="text"
          name="location"
          value={newBook.location}
          onChange={handleChange}
          placeholder="Location"
          required
          className="border rounded p-2 mr-2 text-black"
        />
        <button type="submit" className="bg-blue-500 text-white p-2 rounded">Add Book</button>
      </form>

      <ul>
        {books.map((book) => (
          <li key={book.id} className="flex justify-between items-center mb-2">
            <span>{book.title} by {book.author}</span>
            <button
              onClick={() => handleDelete(book.id)}
              className="bg-red-500 text-white p-1 rounded"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BookManager;
