import React from 'react';

const BookCard = ({ book }) => {
    return (
        <div className="bg-cardBg p-6 rounded-lg shadow-lg hover:shadow-2xl transition duration-300 transform hover:scale-105">
            <h3 className="text-2xl font-bold text-secondary mb-2">{book.title}</h3>
            <h4 className="text-xl text-gray-300 mb-4">{book.author}</h4>
            <p className="text-gray-400 mb-4">{book.description}</p>
            <p className="text-lg text-accent">Genre: {book.genre}</p>
            <p className="text-lg text-lightAccent">Condition: {book.condition}</p>
            <div className="mt-4">
                <button className="bg-primary text-white py-2 px-4 rounded-lg transition duration-300 hover:bg-secondary">
                    Request Exchange
                </button>
            </div>
        </div>
    );
};

export default BookCard;
