import React, { useEffect, useState } from "react";
import { db } from "../../firebase"; // Import Firestore
import { collection, getDocs } from "firebase/firestore";

const BrowseBooks = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBooks = async () => {
      const booksCollection = collection(db, "books");
      const bookSnapshot = await getDocs(booksCollection);
      const bookList = bookSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setBooks(bookList);
      setLoading(false);
    };

    fetchBooks();
  }, []);

  return (
    <div className="min-h-screen bg-black p-4">
      <h2 className="text-3xl font-bold text-center text-white mb-6">Available Books</h2>
      {loading ? (
        <p className="text-white text-center">Loading...</p>
      ) : (
        <ul className="space-y-4">
          {books.map((book) => (
            <li key={book.id} className="bg-gray-800 p-4 rounded-md">
              <h3 className="text-xl text-white">{book.title}</h3>
              <p className="text-gray-400">Author: {book.author}</p>
              <p className="text-gray-400">Condition: {book.condition}</p>
              <p className="text-gray-400">Description: {book.description}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default BrowseBooks;
