import React, { useState } from "react";
import axios from "axios";

const ExchangeBooks = () => {
    const [bookDetails, setBookDetails] = useState({
        offeredBookId: "",
        requestedBookId: "",
        message: "", // Optional message for the exchange request
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setBookDetails((prevDetails) => ({
            ...prevDetails,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:5000/api/exchange", bookDetails);
            console.log("Exchange request submitted:", response.data);
            // Reset form
            setBookDetails({
                offeredBookId: "",
                requestedBookId: "",
                message: "",
            });
        } catch (error) {
            console.error("Error exchanging book:", error);
            alert("Failed to exchange book. Please try again."); // User feedback
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 py-10">
            <h2 className="text-3xl font-bold text-center mb-6">Exchange Your Book</h2>
            <form onSubmit={handleSubmit} className="max-w-md mx-auto bg-white p-8 rounded-md shadow-md">
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">Offered Book ID</label>
                    <input
                        type="text"
                        name="offeredBookId"
                        value={bookDetails.offeredBookId}
                        onChange={handleChange}
                        required
                        className="border rounded w-full py-2 px-3 text-gray-700"
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">Requested Book ID</label>
                    <input
                        type="text"
                        name="requestedBookId"
                        value={bookDetails.requestedBookId}
                        onChange={handleChange}
                        required
                        className="border rounded w-full py-2 px-3 text-gray-700"
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">Message (optional)</label>
                    <textarea
                        name="message"
                        value={bookDetails.message}
                        onChange={handleChange}
                        className="border rounded w-full py-2 px-3 text-gray-700"
                    />
                </div>
                <button
                    type="submit"
                    className="w-full bg-blue-600 text-white font-bold py-2 px-4 rounded hover:bg-blue-700"
                >
                    Exchange Book
                </button>
            </form>
        </div>
    );
};

export default ExchangeBooks;
