// src/components/Signup.jsx
import React, { useState } from 'react';
import { useUserContext } from '../context/UserContext';
import { Link } from 'react-router-dom';

const Signup = () => {
  const { signup } = useUserContext();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    bio: '',
    location: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    signup(formData.email, formData.password, formData.name, formData.bio, formData.location);
  };

  return (
    <div className="flex flex-col items-center">
      <form onSubmit={handleSubmit} className="flex flex-col space-y-4">
        <input name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />
        <input name="email" value={formData.email} onChange={handleChange} placeholder="Email" required />
        <input name="password" value={formData.password} onChange={handleChange} placeholder="Password" type="password" required />
        <input name="bio" value={formData.bio} onChange={handleChange} placeholder="Bio" />
        <input name="location" value={formData.location} onChange={handleChange} placeholder="Location" />
        <button type="submit" className="bg-primary text-white p-2 rounded">Signup</button>
      </form>
      <p className="mt-4">
        Already have an account?{' '}
        <Link to="/login" className="text-primary hover:underline">Login here</Link>
      </p>
    </div>
  );
};

export default Signup;
