import React, { useState } from "react";
import { db } from "../firebase/firebase";
import { collection, addDoc } from "firebase/firestore";
import { useNavigate } from "react-router-dom";

const ListBook = () => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [description, setDescription] = useState("");
  const [condition, setCondition] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleListBook = async (e) => {
    e.preventDefault();
    try {
      await addDoc(collection(db, "books"), {
        title,
        author,
        description,
        condition,
        owner: "user@example.com", // Replace with actual user email from auth
        // Add any other fields you need
      });
      navigate("/");
    } catch (err) {
      setError("Error listing book");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-black">
      <div className="bg-gray-800 p-8 rounded-lg shadow-md w-full max-w-md">
        <h2 className="text-3xl font-bold text-center text-white mb-6">List a Book</h2>
        {error && <p className="text-red-500 mb-4">{error}</p>}
        <form onSubmit={handleListBook}>
          <div className="mb-4">
            <label className="block text-gray-400 mb-2">Title</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
              className="w-full px-4 py-2 bg-gray-700 text-white rounded-md"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-400 mb-2">Author</label>
            <input
              type="text"
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
              required
              className="w-full px-4 py-2 bg-gray-700 text-white rounded-md"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-400 mb-2">Description</label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
              className="w-full px-4 py-2 bg-gray-700 text-white rounded-md"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-400 mb-2">Condition</label>
            <input
              type="text"
              value={condition}
              onChange={(e) => setCondition(e.target.value)}
              required
              className="w-full px-4 py-2 bg-gray-700 text-white rounded-md"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-2 rounded-md"
          >
            List Book
          </button>
        </form>
      </div>
    </div>
  );
};

export default ListBook;
