import React from 'react';

const BookCardReci = ({ request, onStatusChange }) => {
    const { id, offeredBooks, requestedBookId, status } = request;

    return (
        <div className="card">
            <h3>Request for Book ID: {requestedBookId}</h3>
            <p>Status: <strong>{status}</strong></p>
            <h4>Offered Books:</h4>
            <ul>
                {offeredBooks.map((book, index) => (
                    <li key={index}>{book}</li>
                ))}
            </ul>
            <button onClick={() => onStatusChange(id, 'accepted')} disabled={status === 'accepted'}>
                Accept
            </button>
            <button onClick={() => onStatusChange(id, 'rejected')} disabled={status === 'rejected'}>
                Reject
            </button>
        </div>
    );
};

export default BookCardReci;
