// src/components/UserRequests.jsx
import React, { useEffect, useState } from 'react';
import { useUserContext } from '../context/UserContext';
import axios from 'axios';

const UserRequests = () => {
  const { user } = useUserContext();
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    const fetchUserRequests = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/api/requests/user/${user.uid}`);
        setRequests(response.data);
      } catch (error) {
        console.error("Error fetching user requests:", error);
      }
    };

    fetchUserRequests();
  }, [user.uid]);

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold text-primary">Your Requests</h2>
      {requests.length > 0 ? (
        requests.map(request => (
          <div key={request.id} className="mt-4 p-4 border border-gray-700">
            <h3 className="text-xl">{request.requestedBookId}</h3>
            <p>Offered Books: {request.offeredBooks.join(', ')}</p>
            <p>Status: {request.status}</p>
          </div>
        ))
      ) : (
        <p className="text-gray-500">No requests made yet.</p>
      )}
    </div>
  );
};

export default UserRequests;
