// src/MainContent.jsx
import React, { useState } from 'react';
import { motion } from 'framer-motion';

const MainContent = () => {
  const [isBrowsing, setIsBrowsing] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  const handleBrowseClick = () => {
    setIsBrowsing(true);
  };

  return (
    <main className="bg-white min-h-screen py-12">
      <div className="container mx-auto px-6">
        {/* Conditional rendering based on browsing state */}
        {!isBrowsing ? (
          <>
            <div className="flex justify-center mb-8 space-x-4">
              <button className="bg-gradient-to-r from-purple-600 to-indigo-700 text-white font-bold py-3 px-6 rounded-full shadow-lg hover:shadow-xl hover:scale-105 transition duration-300">
                Receiving Requests
              </button>

              <button className="bg-gradient-to-r from-orange-500 to-red-600 text-white font-bold py-3 px-6 rounded-full shadow-lg hover:shadow-xl hover:scale-105 transition duration-300">
                My Requests
              </button>

              <button
                onClick={handleBrowseClick} // Update this button to trigger browsing
                className="bg-gradient-to-r from-teal-600 to-green-700 text-white font-bold py-3 px-6 rounded-full shadow-lg hover:shadow-xl hover:scale-105 transition duration-300"
              >
                Add Listing
              </button>
            </div>

            <div className="flex justify-center mb-10">
              <input
                type="text"
                placeholder="Search for books..."
                className="w-full max-w-3xl px-6 py-3 text-gray-700 border rounded-full shadow-md focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all duration-300 transform focus:shadow-xl focus:scale-105"
              />
            </div>

            <section className="text-center bg-white rounded-lg p-8 mb-12">
              <h2 className="text-4xl font-extrabold text-purple-700 mb-6">Explore New Books & Share Your Collection!</h2>
              <p className="text-lg text-gray-600 mb-10">Discover new reads, exchange books, and be a part of our growing community of book enthusiasts.</p>
              <a
                onClick={handleBrowseClick}
                className="inline-block px-8 py-4 bg-purple-600 text-white font-bold rounded-full shadow-lg hover:bg-purple-700 transition-all duration-300 transform hover:scale-110 cursor-pointer"
              >
                Browse Collection
              </a>
            </section>
          </>
        ) : (
          // Display search bar and book cards when browsing
          <>
            <motion.div
              initial={{ y: -50, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.5 }}
              className="flex justify-center mb-10"
            >
              <input
                type="text"
                placeholder="Search for books..."
                className="w-full max-w-3xl px-6 py-3 text-gray-700 border rounded-full shadow-md focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all duration-300 transform focus:shadow-xl focus:scale-105"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)} // Update search term on input change
              />
            </motion.div>

            <section className="mb-12">
              <h3 className="text-3xl font-bold text-gray-800 mb-6">Featured Books</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                {/* Replace these sample books with dynamic data as needed */}
                {filteredBooks.length > 0 ? (
                  filteredBooks.map(book => (
                    <div key={book.id} className="bg-white rounded-lg shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105 p-5">
                      <img src={book.coverImage} alt="Book Cover" className="rounded-t-lg h-64 w-full object-cover mb-4" />
                      <h4 className="text-xl font-semibold mb-2">{book.title}</h4>
                      <p className="text-gray-600">{book.description}</p>
                      <button className="mt-4 px-6 py-3 bg-blue-500 text-white font-semibold rounded-lg shadow-md hover:bg-blue-600 transform hover:scale-105 transition duration-300">
                        Request Exchange
                      </button>
                    </div>
                  ))
                ) : (
                  <p className="text-gray-500 text-lg">No books found matching your search.</p>
                )}
              </div>
            </section>
          </>
        )}
      </div>
    </main>
  );
};

export default MainContent;
