// src/MainContent.jsx
import React from 'react';
import { Link } from 'react-router-dom';
// import UserRequests from './components/UserRequests'; // Import UserRequests component

const MainContent = () => {
  return (
    // <main className="bg-background text-white py-16">
    //   <section className="container mx-auto text-center">
    //     <h1 className="text-5xl font-extrabold text-primary mb-6">Discover & Exchange Books</h1>
    //     <p className="mb-12 text-xl text-lightAccent">Join the community and share the joy of reading.</p>

    //     <div className="mb-12">
    //       <input
    //         type="text"
    //         placeholder="Search for books..."
    //         className="p-3 w-1/2 rounded-lg bg-gray-700 text-white shadow-lg focus:outline-none focus:ring-2 focus:ring-primary"
    //       />
    //     </div>

    //     <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
    //       <div className="bg-cardBg p-8 rounded-lg shadow-lg hover:shadow-2xl transition duration-300 transform hover:scale-105">
    //         <h2 className="text-3xl font-bold text-secondary mb-4">Browse Books</h2>
    //         <p className="text-gray-400">Find a variety of books available for exchange or donation.</p>
    //       </div>
    //       <div className="bg-cardBg p-8 rounded-lg shadow-lg hover:shadow-2xl transition duration-300 transform hover:scale-105">
    //         <h2 className="text-3xl font-bold text-accent mb-4">List a Book</h2>
    //         <p className="text-gray-400">Share the books you no longer need with others.</p>
    //       </div>
    //       <div className="bg-cardBg p-8 rounded-lg shadow-lg hover:shadow-2xl transition duration-300 transform hover:scale-105">
    //         <h2 className="text-3xl font-bold text-lightAccent mb-4">Your Profile</h2>
    //         <p className="text-gray-400">Manage your book listings and exchange requests.</p>
    //         {/* Add a button or link to the UserRequests component */}
    //         <Link to="/Viewrequests" className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition duration-200">
    //           View Your Requests
    //         </Link>

    //         <Link to="/recievedRequests" className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition duration-200">
    //           reciever Requests
    //         </Link>
    //       </div>
    //     </div>
    //   </section>

    //   <section className="container mx-auto mt-16">
    //     <h2 className="text-4xl font-bold text-primary mb-8">Popular Categories</h2>
    //     <div className="flex justify-center space-x-8">
    //       <span className="bg-secondary text-white p-4 rounded-lg shadow-lg hover:bg-primary transition duration-300">Fiction</span>
    //       <span className="bg-secondary text-white p-4 rounded-lg shadow-lg hover:bg-primary transition duration-300">Non-Fiction</span>
    //       <span className="bg-secondary text-white p-4 rounded-lg shadow-lg hover:bg-primary transition duration-300">Academic</span>
    //       <span className="bg-secondary text-white p-4 rounded-lg shadow-lg hover:bg-primary transition duration-300">Biography</span>
    //     </div>
    //   </section>
    // </main>



    <main class="bg-white min-h-screen py-12">
      <div class="container mx-auto px-6">


        <div class="flex justify-center mb-6 space-x-6">
          <a href="#" class="bg-gradient-to-r from-green-400 to-blue-400 text-white px-6 py-3 rounded-full font-bold shadow-lg transform transition-all duration-300 hover:scale-110 hover:shadow-2xl">My Requests</a>
          <a href="#" class="bg-gradient-to-r from-red-400 to-pink-400 text-white px-6 py-3 rounded-full font-bold shadow-lg transform transition-all duration-300 hover:scale-110 hover:shadow-2xl">Receiving Requests</a>
        </div>


        <div class="flex justify-center mb-10">
          <input type="text" placeholder="Search for books..." class="w-full max-w-3xl px-6 py-3 text-gray-700 border rounded-full shadow-md focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all duration-300 transform focus:shadow-xl focus:scale-105"/>
        </div>


        <section class="text-center bg-white rounded-lg p-8 mb-12">
          <h2 class="text-4xl font-extrabold text-purple-700 mb-6">Explore New Books & Share Your Collection!</h2>
          <p class="text-lg text-gray-600 mb-10">Discover new reads, exchange books, and be a part of our growing community of book enthusiasts.</p>
          <a href="#" class="inline-block px-8 py-4 bg-purple-600 text-white font-bold rounded-full shadow-lg hover:bg-purple-700 transition-all duration-300 transform hover:scale-110">Browse Collection</a>
        </section>


        <section class="mb-12">
          <h3 class="text-3xl font-bold text-gray-800 mb-6">Featured Books</h3>
          <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            <div class="bg-white rounded-lg shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105 p-5">
              <img src="https://images.unsplash.com/photo-1524995997946-a1c2e315a42f" alt="Book Cover" class="rounded-t-lg h-64 w-full object-cover mb-4"/>
              <h4 class="text-xl font-semibold mb-2">The Great Gatsby</h4>
              <p class="text-gray-600">A classic novel by F. Scott Fitzgerald.</p>
              <button class="mt-4 px-6 py-3 bg-blue-500 text-white font-semibold rounded-lg shadow-md hover:bg-blue-600 transform hover:scale-105 transition duration-300">
                Request Exchange
              </button>
            </div>

            <div class="bg-white rounded-lg shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105 p-5">
              <img src="https://images.unsplash.com/photo-1512820790803-83ca734da794" alt="Book Cover" class="rounded-t-lg h-64 w-full object-cover mb-4"/>
              <h4 class="text-xl font-semibold mb-2">To Kill a Mockingbird</h4>
              <p class="text-gray-600">A story of racial injustice by Harper Lee.</p>
              <button class="mt-4 px-6 py-3 bg-blue-500 text-white font-semibold rounded-lg shadow-md hover:bg-blue-600 transform hover:scale-105 transition duration-300">
                Request Exchange
              </button>
            </div>

            <div class="bg-white rounded-lg shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105 p-5">
              <img src="https://images.unsplash.com/photo-1532012197276-a07f4a4885bf" alt="Book Cover" class="rounded-t-lg h-64 w-full object-cover mb-4"/>
              <h4 class="text-xl font-semibold mb-2">1984</h4>
              <p class="text-gray-600">A dystopian novel by George Orwell.</p>
              <button class="mt-4 px-6 py-3 bg-blue-500 text-white font-semibold rounded-lg shadow-md hover:bg-blue-600 transform hover:scale-105 transition duration-300">
                Request Exchange
              </button>
            </div>
          </div>
        </section>


        <section class="bg-purple-50 py-10 rounded-lg shadow-lg mb-12">
          <h3 class="text-3xl font-bold text-gray-800 mb-8 text-center">What Our Users Say</h3>
          <div class="flex flex-col md:flex-row items-center justify-around gap-8">
            <div class="max-w-md p-6 bg-white rounded-lg shadow-md hover:shadow-lg transform hover:scale-105 transition-all duration-300">
              <p class="text-gray-600 mb-4">"This platform is amazing. I've found rare books here and connected with many book lovers!"</p>
              <h4 class="font-semibold text-lg text-purple-700">- Emily Clarke</h4>
            </div>
            <div class="max-w-md p-6 bg-white rounded-lg shadow-md hover:shadow-lg transform hover:scale-105 transition-all duration-300">
              <p class="text-gray-600 mb-4">"Easy to use, great community, and a fantastic way to explore new genres."</p>
              <h4 class="font-semibold text-lg text-purple-700">- Michael Smith</h4>
            </div>
            <div class="max-w-md p-6 bg-white rounded-lg shadow-md hover:shadow-lg transform hover:scale-105 transition-all duration-300">
              <p class="text-gray-600 mb-4">"I have exchanged more than 20 books here. The platform is very user-friendly."</p>
              <h4 class="font-semibold text-lg text-purple-700">- Sarah Johnson</h4>
            </div>
          </div>
        </section>
      </div>
    </main>



  );
};

export default MainContent;
