// src/components/Signup.jsx
import React, { useState } from 'react';
import { useUserContext } from '../context/UserContext';

const Signup = () => {
  const { signup } = useUserContext();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    bio: '',
    location: '',
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await signup(formData.email, formData.password, formData.name, formData.bio, formData.location);
      setSuccessMessage('Signup successful! You can now log in.');
      setFormData({ email: '', password: '', name: '', bio: '', location: '' }); // Clear form
    } catch (error) {
      setErrorMessage('Signup failed. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col space-y-4">
      <input
        name="name"
        value={formData.name}
        onChange={handleChange}
        placeholder="Name"
        className="p-2 border border-gray-300 rounded"
        style={{ color: 'black' }} // Set input text color to black
        required
      />
      <input
        name="email"
        value={formData.email}
        onChange={handleChange}
        placeholder="Email"
        className="p-2 border border-gray-300 rounded"
        style={{ color: 'black' }} // Set input text color to black
        required
      />
      <input
        name="password"
        value={formData.password}
        onChange={handleChange}
        placeholder="Password"
        type="password"
        className="p-2 border border-gray-300 rounded"
        style={{ color: 'black' }} // Set input text color to black
        required
      />
      <input
        name="bio"
        value={formData.bio}
        onChange={handleChange}
        placeholder="Bio"
        className="p-2 border border-gray-300 rounded"
        style={{ color: 'black' }} // Set input text color to black
      />
      <input
        name="location"
        value={formData.location}
        onChange={handleChange}
        placeholder="Location"
        className="p-2 border border-gray-300 rounded"
        style={{ color: 'black' }} // Set input text color to black
      />
      <button type="submit" className="bg-primary text-white p-2 rounded">
        Signup
      </button>
      {errorMessage && <p className="text-red-500">{errorMessage}</p>} {/* Display error message */}
      {successMessage && <p className="text-green-500">{successMessage}</p>} {/* Display success message */}
    </form>
  );
};

export default Signup;
