// src/components/HeroSection.jsx
import React from "react";

const HeroSection = () => {
    return (
        <div className="flex flex-col items-center justify-center bg-blue-600 text-white py-20">
            <h2 className="text-4xl font-extrabold">Join the Book Exchange Community!</h2>
            <p className="mt-4 text-lg">Share your favorite books and discover new reads in a sustainable way.</p>
            <button className="mt-6 bg-white text-blue-600 px-4 py-2 rounded-md font-semibold hover:bg-gray-200">
                Get Started
            </button>
        </div>
    );
};

export default HeroSection;
