// src/components/BookManager.jsx
import React, { useState, useEffect } from "react";
import { addBook, getBooks } from "../database"; // Adjust this path as needed

const BookManager = () => {
  const [newBook, setNewBook] = useState({
    title: "",
    author: "",
    description: "",
    condition: "",
    owner: "", // This should ideally come from the logged-in user's info
    category: "",
    photos: [],
    location: ""
  });

  const [books, setBooks] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewBook((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddBook = async (e) => {
    e.preventDefault();
    try {
      await addBook(newBook);
      setNewBook({
        title: "",
        author: "",
        description: "",
        condition: "",
        owner: "",
        category: "",
        photos: [],
        location: ""
      });
      fetchBooks(); // Refresh the book list after adding a new book
    } catch (error) {
      console.error("Error adding book: ", error);
    }
  };

  const fetchBooks = async () => {
    try {
      const fetchedBooks = await getBooks();
      setBooks(fetchedBooks);
    } catch (error) {
      console.error("Error fetching books: ", error);
    }
  };

  useEffect(() => {
    fetchBooks(); // Fetch books when the component mounts
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Add a New Book</h2>
      <form onSubmit={handleAddBook} className="mb-4">
        <input
          type="text"
          name="title"
          value={newBook.title}
          onChange={handleInputChange}
          placeholder="Title"
          required
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          name="author"
          value={newBook.author}
          onChange={handleInputChange}
          placeholder="Author"
          required
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          name="description"
          value={newBook.description}
          onChange={handleInputChange}
          placeholder="Description"
          required
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          name="condition"
          value={newBook.condition}
          onChange={handleInputChange}
          placeholder="Condition"
          required
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          name="category"
          value={newBook.category}
          onChange={handleInputChange}
          placeholder="Category"
          required
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          name="location"
          value={newBook.location}
          onChange={handleInputChange}
          placeholder="Location"
          required
          className="border p-2 mb-2 w-full"
        />
        <button
          type="submit"
          className="bg-green-600 text-white p-2 rounded"
        >
          Add Book
        </button>
      </form>
      <h2 className="text-2xl font-bold mb-4">Available Books</h2>
      <ul>
        {books.map((book) => (
          <li key={book.id} className="border p-2 mb-2">
            <h3 className="font-bold">{book.title}</h3>
            <p>Author: {book.author}</p>
            <p>Description: {book.description}</p>
            <p>Condition: {book.condition}</p>
            <p>Location: {book.location}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BookManager;
