// src/components/BookCard.jsx
import React from "react";

const BookCard = ({ book }) => {
    return (
        <div className="max-w-xs rounded overflow-hidden shadow-lg bg-white m-4">
            <img className="w-full h-48 object-cover" src={book.image} alt={book.title} />
            <div className="px-6 py-4">
                <div className="font-bold text-xl mb-2">{book.title}</div>
                <p className="text-gray-700 text-base">{book.description}</p>
            </div>
            <div className="px-6 pt-4 pb-2">
                <span className="inline-block bg-blue-200 rounded-full px-3 py-1 text-sm font-semibold text-blue-800 mr-2 mb-2">
                    {book.category}
                </span>
            </div>
        </div>
    );
};

export default BookCard;
