import React, { useEffect, useState } from "react";
import axios from 'axios';
import { auth } from "../firebase/firebase"; // Assuming Firebase auth is set up

const BookManager = () => {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({
    title: "Atomic Habits",
    author: "James Clear",
    description: "Based on habit",
    condition: "Good",
    owner: "", // We'll set this with the logged-in user's ID
    category: "Self Growth",
    photos: ['./index.jpg'],
    location: "Surat",
  });

  // Fetch books on component mount
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        // Get current user ID from Firebase
        const user = auth.currentUser;
        if (user) {
          const userId = user.uid;
          console.log("Current User ID:", userId); // Check if userId is being set

          const response = await axios.get(`http://localhost:4000/api/books/${userId}`);
          setBooks(response.data);
        }
      } catch (error) {
        console.error("Error fetching books:", error);
      }
    };

    fetchBooks();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewBook((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Get current user ID
      const user = auth.currentUser;
      if (user) {
        const userId = user.uid;
        const bookData = { ...newBook, owner: userId };

        await axios.post('http://localhost:4000/api/books', bookData);
        setBooks((prev) => [...prev, bookData]);

        // Reset form after adding book
        setNewBook({
          title: "",
          author: "",
          description: "",
          condition: "",
          owner: "",
          category: "",
          photos: [],
          location: "",
        });
      }
    } catch (error) {
      console.error("Error adding book:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/books/${id}`);
      setBooks((prev) => prev.filter((book) => book.id !== id));
    } catch (error) {
      console.error("Error deleting book:", error);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Manage Your Books</h2>

      <form onSubmit={handleSubmit} className="mb-4">
        <input
          type="text"
          name="title"
          value={newBook.title}
          onChange={handleChange}
          placeholder="Title"
          required
          className="border rounded p-2 mr-2"
        />
        <input
          type="text"
          name="author"
          value={newBook.author}
          onChange={handleChange}
          placeholder="Author"
          required
          className="border rounded p-2 mr-2"
        />
        <textarea
          name="description"
          value={newBook.description}
          onChange={handleChange}
          placeholder="Description"
          required
          className="border rounded p-2 mr-2 w-full"
        />
        <input
          type="text"
          name="condition"
          value={newBook.condition}
          onChange={handleChange}
          placeholder="Condition"
          required
          className="border rounded p-2 mr-2"
        />
        <input
          type="text"
          name="category"
          value={newBook.category}
          onChange={handleChange}
          placeholder="Category"
          required
          className="border rounded p-2 mr-2"
        />
        <input
          type="text"
          name="location"
          value={newBook.location}
          onChange={handleChange}
          placeholder="Location"
          required
          className="border rounded p-2 mr-2"
        />
        <button type="submit" className="bg-blue-500 text-white p-2 rounded">Add Book</button>
      </form>

      <ul>
        {books.map((book) => (
          <li key={book.id} className="flex justify-between items-center mb-2">
            <span>{book.title} by {book.author}</span>
            <button
              onClick={() => handleDelete(book.id)}
              className="bg-red-500 text-white p-1 rounded"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BookManager;
