// src/components/BookManager.jsx
import React, { useEffect, useState } from "react";
import { addBook, getBooks } from "../database"; // Adjust the path based on your folder structure

const BookManager = () => {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({
    title: "",
    author: "",
    description: "",
    condition: "",
    owner: "", // Assume owner is the user's ID
    category: "",
    photos: [],
    location: ""
  });

  const fetchBooks = async () => {
    const fetchedBooks = await getBooks();
    setBooks(fetchedBooks);
  };

  const handleAddBook = async (e) => {
    e.preventDefault();
    await addBook(newBook);
    fetchBooks(); // Refresh the book list after adding a new book
  };

  useEffect(() => {
    fetchBooks(); // Fetch books when the component mounts
  }, []);

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">Add a New Book</h2>
      <form onSubmit={handleAddBook} className="mb-4">
        <input
          type="text"
          placeholder="Title"
          value={newBook.title}
          onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
          required
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          placeholder="Author"
          value={newBook.author}
          onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
          required
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          placeholder="Description"
          value={newBook.description}
          onChange={(e) => setNewBook({ ...newBook, description: e.target.value })}
          required
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          placeholder="Condition"
          value={newBook.condition}
          onChange={(e) => setNewBook({ ...newBook, condition: e.target.value })}
          required
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          placeholder="Category"
          value={newBook.category}
          onChange={(e) => setNewBook({ ...newBook, category: e.target.value })}
          required
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          placeholder="Location"
          value={newBook.location}
          onChange={(e) => setNewBook({ ...newBook, location: e.target.value })}
          required
          className="border p-2 mb-2 w-full"
        />
        <button type="submit" className="bg-blue-500 text-white p-2">
          Add Book
        </button>
      </form>

      <h2 className="text-2xl font-bold mb-4">Available Books</h2>
      <ul>
        {books.map((book) => (
          <li key={book.id} className="border p-2 mb-2">
            <h3 className="font-bold">{book.title}</h3>
            <p>{book.author}</p>
            <p>{book.description}</p>
            <p>Condition: {book.condition}</p>
            <p>Category: {book.category}</p>
            <p>Location: {book.location}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BookManager;
