import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useState } from 'react';
import Home from './components/MainContent';
import Signup from './components/Signup';
import Login from './components/Login';
import Header from './components/Header';
import ListBook from './components/ListBook';
import BrowseBooks from './components/BrowseBooks'; // Import the new component
import { auth, logout } from './firebase/firebase';
import { onAuthStateChanged } from 'firebase/auth';

function App() {
  const [user, setUser] = useState(null);

  // Listen for auth state changes
  onAuthStateChanged(auth, (currentUser) => {
    setUser(currentUser);
  });

  return (
    <div className="min-h-screen bg-black text-white">
      <Router>
        <Header user={user} logout={logout} />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login />} />
          <Route path="/list" element={<ListBook />} />  {/* Route for listing books */}
          <Route path="/browse" element={<BrowseBooks />} />  {/* Route for browsing books */}
        </Routes>
      </Router>
    </div>
  );
}

export default App;
